insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (515, 'Prodajni inženjer (m/ž)', 18, 8, 'https://www.moj-posao.net/Posao/482000/Prodajni-inzenjer-mz/', 'Više od dvadeset godina poslujemo u Hrvatskoj i regiji na području implementacije industrijskih sustava označavanja proizvoda i ambalaže, uz konzalting i izvedbu projekata sljedivosti visoke tehnološke razine. Regionalni smo integracijski partneri s tvrtkama TELESIS, TOSHIBA, REA-JET, ARCA, ZEBRA, VALCO MELTON i drugima, koje svaka u svom području predstavljaju tehnološki vrh svjetske ponude. 
