insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (108, 'Suradnik za upravljanje proizvodima za stanovništvo i podršku prodaji (m/ž)', 3, 149, 'https://www.moj-posao.net/Posao/520087/Suradnik-za-upravljanje-proizvodima-za-stanovnistvo-i-podrsku-prodaji-mz/', 'KentBank d.d., moderna, univerzalna banka fokusirana na bitno, jednostavno i efikasno zadovoljavajući potrebe klijenata kojima na raspolaganju stoji 15 poslovnica diljem Hrvatske, traži:
