insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (184, 'Tehničar za elektroniku (m/ž)', 6, 253, 'https://www.moj-posao.net/Posao/519247/Tehnicar-za-elektroniku-mz/', 'Tehničar za elektroniku (m/ž)  Zagreb Proizvodnja LED rasvjete pod brendom LEDA dio je portfelja poduzeća Omega software d.o.o.
