insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (3, 'Voditelj Odjela za udružene i strateške projekte (m/ž)', 1, 3, 'https://www.moj-posao.net/Posao/505630/Voditelj-Odjela-za-udruzene-i-strateske-projekte-mz/', 'Hrvatska turistička zajednica objavljuje natječaj za primanje u radni odnos na radno mjesto:

