insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (121, 'Supervizor za bonitetni nadzor poslovanja kreditnih institucija (m/ž)', 3, 164, 'https://www.moj-posao.net/Posao/505507/Supervizor-za-bonitetni-nadzor-poslovanja-kreditnih-institucija-mz/', 'Hrvatska narodna banka središnja je banka Republike Hrvatske i sastavni dio Europskog sustava središnjih banaka. Svrha je HNB-a postizanje stabilnosti, pouzdanosti i sigurnosti cjelokupnoga hrvatskog financijskog sustava.
