insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (465, 'Country Approval Specialist (m/f)', 7, 516, 'https://www.moj-posao.net/Poslovac/63605/Country-Approval-Specialist-mf/', 'Opis posla
