insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (2520, 'Logistics &amp; Shipping Officer (m/f)', 20, 247, 'https://www.moj-posao.net/Posao/504238/Logistics-Shipping-Officer-mf/', 'Opis posla
