insert into oglasi (id, naziv, kat_id, pos_id, url, opis_posla, rok_prijave) values (2636, 'Voditelj izrade izvedbene dokumentacije strukture broda u 3D CAD sustavu (m/ž)', 24, 2229, 'https://www.moj-posao.net/Posao/505012/Voditelj-izrade-izvedbene-dokumentacije-strukture-broda-u-3D-CAD-sustavu-mz/', 'Navis Consult d.o.o. sa sjedištem u Rijeci članica je norveške grupacije KONGSBERG (www.kongsberg.com) i već 25 godina djeluje na tržištu inženjerskih usluga za brodogradnju uz kontinuiran rast i uspješno poslovanje. 
